import React, { Component } from 'react'
import Todos from './Todos'

class App extends Component {
	state = {
		todos: [
			{ id: 1, content: 'Manger' },
			{ id: 2, content: 'Voir Louise' }
		]
	}
	deleteTodo = (id) => {
		console.log(id)
	}

	render() {
		return (
			<div className="App">
				<Todos
					todos={this.state.todos}
					deleteTodo={this.state.deleteTodo}
				/>
			</div>
		)
	}
}

export default App
