import React from 'react'

const todos = ({ todos }) => {
	if (todos.length) {
		const todoList = todos.map((todo) => {
			return (
				<div key={todo.id}>
					<p>{todo.content}</p>
				</div>
			)
		})
	} else {
		return <p>No todo !</p>
	}
	return <div>{todoList}</div>
}

export default todos
