import React from 'react'

const todos = ({ todos, deleteTodo }) => {
	if (todos.length) {
		return todos.map((todo) => {
			return (
				<div key={todo.id}>
					<p
						onClick={() => {
							deleteTodo(todo.id)
						}}>
						{todo.content}
					</p>
				</div>
			)
		})
	} else {
		return <p>No todo !</p>
	}
}

export default todos
