import React, { useContext } from 'react'
import { TodoContext } from '../contexts/TodoContext'

const TodoList = () => {
	const { todos, dispatch } = useContext(TodoContext)

	return todos.length ? (
		<ul>
			{todos.map((todo) => {
				return (
					<li
						key={todo.id}
						onClick={() =>
							dispatch({ type: 'REMOVE_TODO', id: todo.id })
						}>
						{todo.text}
					</li>
				)
			})}
		</ul>
	) : (
		<p>No todo ! :)</p>
	)
}

export default TodoList
