import React, { useContext } from 'react'
import { TodoContext } from '../contexts/TodoContext'

const FilterTodo = () => {
	const { filter, dispatchFilter } = useContext(TodoContext)

	const handleCheck = () => {
		setFilter({ include: filter.include, completed: !filter.completed })
	}

	const handleInclude = (e) => {
		setFilter({ include: e.target.value, completed: filter.completed })
	}

	return (
		<div>
			<input type="checkbox" onChange={() => handleCheck()} />
			<input
				type="text"
				onChange={(e) =>
					dispatchFilter({
						type: 'UPDATE_INCLUDE',
						include: e.target.value
					})
				}
			/>
		</div>
	)
}

export default FilterTodo
