import React, { useContext } from 'react'
import { TodoContext } from '../contexts/TodoContext'

const TodoList = () => {
	const { todos, removeTodo } = useContext(TodoContext)

	return todos.length ? (
		<ul>
			{todos.map((todo) => {
				return (
					<li key={todo.id} onClick={() => removeTodo(todo.id)}>
						{todo.text}
					</li>
				)
			})}
		</ul>
	) : (
		<p>no more todo</p>
	)
}

export default TodoList
