import React, { useContext } from 'react'
import { TodoContext } from '../contexts/TodoContext'
import TodoDetails from './TodoDetails'

const TodoList = () => {
	const { todos, filter } = useContext(TodoContext)

	const applyFilter = () => {
		const todos_include = todos.map((todo) =>
			todo.text.include(filter.include)
		)

		return todos_include
	}

	return todos.length ? (
		<ul>
			{todos.map((todo) => {
				return <TodoDetails todo={todo} key={todo.id} />
			})}
		</ul>
	) : (
		<p>No todo ! :)</p>
	)
}

export default TodoList
