import React, { useContext } from 'react'
import { TodoContext } from '../contexts/TodoContext'
import TodoDetails from './TodoDetails'

const TodoList = () => {
	const { todos } = useContext(TodoContext)

	return todos.length ? (
		<ul>
			{todos.map((todo) => {
				return <TodoDetails todo />
			})}
		</ul>
	) : (
		<p>No todo ! :)</p>
	)
}

export default TodoList
